import React from 'react';

import { useParams } from 'react-router-dom';
import { Stack, CircularProgress, Container } from '@mui/material';
import LoremIpsum from 'react-lorem-ipsum';

export default function Resort(props) {
   const [isDataLoaded, setIsDataLoaded] = React.useState(false);

   const { resortQuery } = useParams();
   const skimapID = props.resorts.find(resort => resort.query === resortQuery).skimapID;

   let [resortData, setResortData] = React.useState({});
   const corsProxy = 'https://mighty-scrubland-75189.herokuapp.com';

   React.useEffect(() => {
      fetch(`${corsProxy}/https://liftie.info/api/resort/${resortQuery}`)
         .then(response => response.json())
         .then(data => {
            setResortData(data);
            setIsDataLoaded(true);
         })
         .catch((error) => {
            console.log(`Encountered error in Liftie Fetch request: ${error}`)
         });
   }, [])

   // only display the resort content AFTER the fetch call has returned!
   let displayContent = isDataLoaded ? <>
      <div>You clicked on this ski resort: {resortData.name}</div>
      <div>The ski map ID is {skimapID}</div></> : <Container style={{
         display: 'flex',
         justifyContent: 'center',
         alignItems: 'center',
         margin: '4.8rem'
      }}>
      <CircularProgress color='inherit' />
   </Container>


   return (
      displayContent
   );
}
