import React from 'react';
import { Paper } from '@mui/material';

import './App.css';
import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import Body from './components/Body/Body';
import Resorts from './components/Resorts/Resorts';

export default function App() {
   const [isBodyLoading, setIsBodyLoading] = React.useState(true);
   const resorts = [{ query: 'boreal', skimapID: '523' },
   { query: 'chinapeak', skimapID: '527' },
   { query: 'diamondpeak', skimapID: '359' },
   { query: 'heavenly', skimapID: '537' },
   { query: 'northstar', skimapID: '531' },
   { query: 'kirkwood', skimapID: '544' },
   { query: 'homewood', skimapID: '545' },
   { query: 'june-mountain', skimapID: '540' },
   { query: 'mammoth-lakes', skimapID: '530' },
   { query: 'mtrose', skimapID: '358' },
   { query: 'palisades', skimapID: '538' },
   { query: 'alpine', skimapID: '522' },
   { query: 'sierra', skimapID: '525' },
   { query: 'sugarbowl', skimapID: '543' },
   { query: 'tahoe-donner', skimapID: '529' },]

   const skiMapData = [];

   const corsProxy = 'https://mighty-scrubland-75189.herokuapp.com';

   React.useEffect(() => {
      resorts.forEach((resort, index) => {
         fetch(`${corsProxy}/https://liftie.info/api/resort/${resort.query}`)
            .then(response => response.json())
            .then(data => {
               const removeProp = 'id';
               const { [removeProp]: remove, ...rest } = data;
               resorts[index] = { ...resorts[index], ...rest };
            })
            .catch((error) => {
               console.log(`Encountered error in Liftie Fetch request: ${error}`)
            });

         // fetch(`${corsProxy}/https://skimap.org/SkiAreas/view/${resort.skimapID}.json`)
         //    .then(response => response.json())
         //    .then(data => {
         //       skiMapData[index] = data;
         //    })
         //    .catch((error) => {
         //       console.log(`Encountered error in SkiMap.org Fetch request: ${error}`)
         //    });
      });
      setIsBodyLoading(previsBodyLoading => !previsBodyLoading);
   }, []);
   // console.log(skiMapData);

   return (
      <Paper>
         <Header />
         {isBodyLoading} ? <div>Loading...</div> :
         <Resorts liftieData={resorts} skimapData={skiMapData} />
         <Footer />
      </Paper>
   )
};
