                  <Typography gutterBottom variant='h4' component='div'>
                     {resort.query}
                  </Typography>
                  <Typography variant='body2' color='text.secondary'>
                     {resort.skimapID}
                  </Typography>
               </CardContent>
            </Card>
         </Grid>
      )
   })
   const numbers = [1, 2, 3, 4, 5];
   const listItems = numbers.map((number) =>
      <div>{number}</div>
