import React from 'react';
import { Box, Paper, Button } from '@mui/material';

export default function Traffic() {
   const destinations = ['Truckee,CA', 'SouthLakeTahoe,CA', 'MammothMountain,CA'];
   const [userCurrentLocation, setUserCurrentLocation] = React.useState({
      long: 0,
      lat: 0
   })
   let maps;

   const divStyling = {
      marginBottom: '1.2rem',
   };

   const buttonStyling = {
      color: '#2d4850'
   };

   const iframeStyle = {
      marginTop: '2rem',
      // removing the border per the doc's example - i thought the border 
      // was too distracting
      border: 0,
      borderRadius: '20px',
   }


   function success(pos) {
      const crd = pos.coords;

      console.log(`Latitude: ${crd.latitude}`);
      console.log(`Longitude: ${crd.longitude}`);
      // setUserCurrentLocation({ data: 0 });
      console.log(userCurrentLocation)

      //    maps = destinations.map(destination => {
      //       return <iframe
      //          style={iframeStyle}
      //          width="600"
      //          height="450"
      //          loading='lazy'
      //          allow="fullscreen"
      //          referrerpolicy="no-referrer-when-downgrade"
      //          src={`https://www.google.com/maps/embed/v1/directions?key=${process.env.REACT_APP_GOOGLE_MAPS_EMBED_API_KEY}
      // &origin=${userCurrentLocation.lat},${userCurrentLocation.long}&destination=${destination}`}>
      //       </iframe>
      //    });
   }

   function error(err) {
      console.warn(`ERROR(${err.code}): ${err.message}`);
   }
   navigator.geolocation.getCurrentPosition(success, error);
   maps = destinations.map(destination => {
      return <iframe
         style={iframeStyle}
         width="600"
         height="450"
         loading='lazy'
         allow="fullscreen"
         referrerpolicy="no-referrer-when-downgrade"
         src={`https://www.google.com/maps/embed/v1/directions?key=${process.env.REACT_APP_GOOGLE_MAPS_EMBED_API_KEY}
&origin=${userCurrentLocation.lat},${userCurrentLocation.long}&destination=${destination}`}>
      </iframe>
   });

   return (

      <Box sx={{
         margin: 4,
         display: 'flex',
         justifyContent: 'center',
      }}>
         <Paper sx={{
            padding: 4,
            boxShadow: 10,
            backgroundColor: '#fff',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            flexDirection: 'column',
         }}>
            <div style={divStyling}>Please see Chad Smith's excellent Tahoe road conditions website here: <Button style={buttonStyling} variant="outlined" href="https://tahoeroads.info/" target='_blank' rel='noopener noreferrer'>
               Check road conditions
            </Button></div>
            <ul> <h3>Three main roads into Tahoe from SF Bay Area:</h3>
               <li>Highway 88 --> Goes to Kirkwood </li>
               <li>Highway 50 --> Goes to Sierra at Tahoe and Heavenly </li>
               <li>Highway 80 --> Goes to north shore Tahoe resorts (Sugar Bowl, Northstar, Palisades, Boreal)</li>
            </ul>
            {/* Secured API key to only work with Google Maps Embed API and need to hide it.
            https://betterprogramming.pub/how-to-hide-your-api-keys-c2b952bc07e6*/}
            {!maps === undefined && maps}
            {/* <iframe
               style={iframeStyle}
               width="600"
               height="450"
               loading='lazy'
               allow="fullscreen"
               referrerpolicy="no-referrer-when-downgrade"
               src={`https://www.google.com/maps/embed/v1/directions?key=${process.env.REACT_APP_GOOGLE_MAPS_EMBED_API_KEY}
    &origin=SanJose,CA&destination=Truckee,CA`}>
            </iframe>
            <iframe
               style={iframeStyle}
               width="600"
               height="450"
               loading='lazy'
               allow="fullscreen"
               referrerpolicy="no-referrer-when-downgrade"
               src={`https://www.google.com/maps/embed/v1/directions?key=${process.env.REACT_APP_GOOGLE_MAPS_EMBED_API_KEY}
    &origin=SanJose,CA&destination=SouthLakeTahoe,CA`}>
            </iframe>
            <iframe
               style={iframeStyle}
               width="600"
               height="450"
               loading='lazy'
               allow="fullscreen"
               referrerpolicy="no-referrer-when-downgrade"
               src={`https://www.google.com/maps/embed/v1/directions?key=${process.env.REACT_APP_GOOGLE_MAPS_EMBED_API_KEY}
    &origin=Irvine,CA&destination=MammothMountain,CA`}></iframe> */}
         </Paper>
      </Box >
   )
};
