import React from 'react';

import { Typography, Paper, Stack, Container } from '@mui/material';

export default function TweetPanel(props) {
   const tweetDateStringArray = props.resort.twitter.tweets[0].created_at.split(' ');

   // parse logic for Tweet published hour
   let tweetDateFormatTime = tweetDateStringArray[3].slice(0, 5);
   const tweetDateHour = parseInt(tweetDateFormatTime.slice(0, 2));
   tweetDateFormatTime = tweetDateHour > 12 ?
      (tweetDateHour - 12).toString() + tweetDateFormatTime.slice(2) + ' PM' :
      tweetDateHour + ' AM';
   const tweetDateClean = `${tweetDateStringArray[0]} ${tweetDateFormatTime} - ${tweetDateStringArray[1]} ${tweetDateStringArray[2]}, ${tweetDateStringArray[5]}`;

   return (
      <Paper>
         <Stack>
            <Typography style={{
               fontWeight: 'bold',
            }}>
               {/* 'Mammoth Mountain Mountain Resort' 
            sounds really bad so just catch that */}
               {props.resort.name.includes('Mountain') ? `${props.resort.name} Resort` :
                  `${props.resort.name} Mountain Resort`}
            </Typography>
            <Typography>
               {`@${props.resort.twitter.user}`}
            </Typography>
         </Stack>
         <Container style={{
            marginTop: '1.2rem',
            lineHeight: '1.6rem'
         }}>
            {props.resort.twitter.tweets[0].text}
            {console.log(props.resort.twitter.tweets[0].entities.media)}
         </Container>
         <Typography style={{ marginTop: '1.2rem' }}>
            {tweetDateClean}
         </Typography>
      </Paper>
   );
}
