import React from 'react';
import { Paper } from '@mui/material';
import { Card, CardMedia, CardContent, CardActions, Button, Typography, Grid } from '@mui/material';
import { Routes, Route } from 'react-router-dom';

import './App.css';
import {
   Header,
   Footer,
   About,
   Resorts,
   Traffic,
   HomeBody
} from './components';

import {
   HeavenlyLogo,
   BorealLogo,
   ChinaPeakLogo,
   DiamondPeakLogo,
   HomewoodLogo,
   JuneLogo,
   KirkwoodLogo,
   MammothLogo,
   MtRoseLogo,
   NorthstarLogo,
   PalisadesLogo,
   SierraLogo,
   SugarBowlLogo,
   TahoeDonnerLogo
} from './images';


export default function App() {
   const [isBodyLoading, setIsBodyLoading] = React.useState(true);
   const resorts = [{ query: 'boreal', skimapID: '523', image: BorealLogo },
   { query: 'chinapeak', skimapID: '527', image: ChinaPeakLogo },
   { query: 'diamondpeak', skimapID: '359', image: DiamondPeakLogo },
   { query: 'heavenly', skimapID: '537', image: HeavenlyLogo },
   { query: 'northstar', skimapID: '531', image: NorthstarLogo },
   { query: 'kirkwood', skimapID: '544', image: KirkwoodLogo },
   { query: 'homewood', skimapID: '545', image: HomewoodLogo },
   { query: 'june-mountain', skimapID: '540', image: JuneLogo },
   { query: 'mammoth-lakes', skimapID: '530', image: MammothLogo },
   { query: 'mtrose', skimapID: '358', image: MtRoseLogo },
   { query: 'palisades', skimapID: '538', image: PalisadesLogo },
   { query: 'alpine', skimapID: '522', image: PalisadesLogo },
   { query: 'sierra', skimapID: '525', image: SierraLogo },
   { query: 'sugarbowl', skimapID: '543', image: SugarBowlLogo },
   { query: 'tahoe-donner', skimapID: '529', image: TahoeDonnerLogo },]

   const skiMapData = [];

   const corsProxy = 'https://mighty-scrubland-75189.herokuapp.com';

   // React.useEffect(() => {
   //    resorts.forEach((resort, index) => {
   //       fetch(`${corsProxy}/https://liftie.info/api/resort/${resort.query}`)
   //          .then(response => response.json())
   //          .then(data => {
   //             // const removeProp = 'id';
   //             // const { [removeProp]: remove, ...rest } = data;
   //             resorts[index] = { ...resorts[index], ...data };
   //          })
   //          .catch((error) => {
   //             console.log(`Encountered error in Liftie Fetch request: ${error}`)
   //          });

   //       // fetch(`${corsProxy}/https://skimap.org/SkiAreas/view/${resort.skimapID}.json`)
   //       //    .then(response => response.json())
   //       //    .then(data => {
   //       //       skiMapData[index] = data;
   //       //    })
   //       //    .catch((error) => {
   //       //       console.log(`Encountered error in SkiMap.org Fetch request: ${error}`)
   //       //    });
   //    });
   //    setIsBodyLoading(previsBodyLoading => !previsBodyLoading);
   // }, []);
   // console.log(skiMapData);
   const resortList = resorts.map((resort) => {
      return (
         // the Grid's responsive layout breakpoints rely on a 
         // 12 column layout. 
         // If XS condition, then each element in grid takes entire 12 cols (1 col layout total)
         // If SM condition, then each element in grid takes 6 cols (2 col layout total)
         // If MD condition or above, then each element in grid takes 3 cols (4 col layout total)
         <Grid key={resort.skimapID} item xs={12} sm={6} md={3}>
            <Card sx={{
               maxWidth: 345, m: 1, "&:hover": {
                  boxShadow: 20,
               },
            }}>
               <CardMedia
                  component='img'
                  height='140'
                  image={resort.image}
                  alt="ski resort logo"
               />
               <CardContent>
                  <Typography gutterBottom variant='h4' component='div'>
                     {resort.query}
                  </Typography>
                  <Typography variant='body2' color='text.secondary'>
                     {resort.skimapID}
                  </Typography>
               </CardContent>
            </Card>
         </Grid>
      )
   })

   return (
      <Paper>
         <Header />
         {/* {isBodyLoading ? <div>Loading...</div> :
            <Resorts liftieData={resorts} skimapData={skiMapData} />} */}
         {/* <Resort resort={resorts[0]} /> */}

         <Routes>
            <Route path='/' element={<HomeBody />} />
            <Route path='/about' element={<About />} />
            <Route path='/resorts'
               element={
                  <Grid container my={4}>
                     {resortList}
                  </Grid>} />
            <Route path='/traffic' element={<Traffic />} />
         </Routes>
         <Footer />
      </Paper>
   )
};
