import React from 'react';

import {
   Card, CardMedia, CardContent,
   Typography, Grid, Backdrop, Paper
} from '@mui/material';

import { LoremIpsum } from "react-lorem-ipsum";


export default function Resorts(props) {
   const [isOpen, setIsOpen] = React.useState(false);
   const handleClose = () => {
      setIsOpen(false);
   };
   const handleToggle = () => {
      setIsOpen(prevSetOpen => !prevSetOpen);
   };

   const resortList = props.resorts.map((resort) => {
      return (
         // the Grid's responsive layout breakpoints rely on a 
         // 12 column layout. 
         // If XS condition, then each element in grid takes entire 12 cols (1 col layout total)
         // If SM condition, then each element in grid takes 6 cols (2 col layout total)
         // If MD condition or above, then each element in grid takes 3 cols (4 col layout total)
         <Grid key={resort.skimapID} item
            xs={12} sm={6} md={3}>
            <Card sx={{
               maxWidth: 345,
               m: 1,
               "&:hover": {
                  boxShadow: 20,
               },
               cursor: 'pointer',
            }}
               onClick={handleToggle}>
               <CardMedia
                  component='img'
                  height='140'
                  image={resort.image}
                  alt="ski resort logo"
               />
               <CardContent>
                  <Typography gutterBottom variant='h4' component='div'>
                     {resort.query}
                  </Typography>
                  <Typography variant='body2' color='text.secondary'>
                     {resort.skimapID}
                  </Typography>
               </CardContent>
            </Card>
         </Grid>
      )
   })
   return (
      <Grid container my={4}>
         <div>
            <Backdrop
               sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
               open={isOpen}
               onClick={handleClose}
            >
               <Paper style={{
                  padding: '12px, 24px',
               }}>
                  <div>This is a Paper component.</div>
                  <LoremIpsum
                     p={6}
                  />
               </Paper>
            </Backdrop>
         </div>
         {resortList}
      </Grid>
   )
};
