import React from 'react';

import {
   Grid, Typography,
   Paper, Container,
   Button,
} from '@mui/material';
import SentimentVeryDissatisfiedIcon from '@mui/icons-material/SentimentVeryDissatisfied';

import TrafficMap from '../TrafficMap/TrafficMap';
import WeatherReport from '../WeatherReport/WeatherReport';

export default function ResortDetail(props) {
   const resort = props.resortData;

   return (
      <Grid container spacing={2} style={{
         padding: '2.4rem'
      }}>
         <Grid item xs={12} sm={6}>
            {/* Not all resorts have weather data to use. Ask user to check a 
         nearby resort for more info. */}
            {!resort.weather ? <div style={{
               display: 'flex',
               alignItems: 'center',
               justifyContent: 'space-between',
            }}><div>No Weather Data Provided
               </div><SentimentVeryDissatisfiedIcon /></div> :
               <WeatherReport resort={resort} />}
         </Grid>
         <Grid item xs={12} sm={6} style={{
            display: 'flex',
            justifyContent: 'end',
         }}>
            <TrafficMap resortName={resort.name}
               mapMode={'place'}
               lat={resort.ll[1]}
               long={resort.ll[0]} />
         </Grid>
         <Grid item xs={12} sm={6}>Lift Status goes here</Grid>
         <Grid item xs={12} sm={6}>Twitter Feed goes here</Grid>
      </Grid>
   );
}
