import React from 'react';

import {
   Grid, Typography,
   Paper, Container,
   Button,
} from '@mui/material';
import TrafficMap from '../TrafficMap/TrafficMap';

export default function ResortDetail(props) {
   const resort = props.resortData;
   const todayStringArray = resort.weather.date.split('-');

   return (
      <Grid container spacing={2} style={{
         padding: '2.4rem'
      }}>
         <Grid item xs={12} sm={6}>
            <Typography variant='h4'
               style={{
                  textDecoration: 'underline',
                  marginBottom: '1.2rem'
               }}>{resort.name}
            </Typography>
            <Paper style={{ padding: '1.2rem 2.4rem' }}>
               {/* We in U.S.A format our dates as MM/DD/YYYY, while the Europeans format as DD/MM/YYYY */}
               <Container style={{
                  display: 'flex',
                  alignItems: 'center',
               }}>
                  <h3>Weather Report for Today, {todayStringArray[1]}/{todayStringArray[2]}/{todayStringArray[0]}</h3>
                  <Container style={{
                     display: 'flex',
                     flexDirection: 'column',
                     alignItems: 'flex-end',
                     maxWidth: '150px',
                  }}>
                     <span>Brought to you by</span>
                     <img src={resort.weather.notice.img} alt='NOAA official logo'
                        width='100' height='50'
                     />
                  </Container>
               </Container>
               <p style={{
                  fontStyle: 'italic',
                  marginTop: '1.0rem',
               }}>{`"${resort.weather.text}"`}</p>
               <Button sx={{ color: '#ed6c02', backgroundColor: '#2d4850', }} variant="contained" href="https://tahoeroads.info/" target='_blank' rel='noopener noreferrer'>
                  Check road conditions
               </Button>
            </Paper>
         </Grid>
         <Grid item xs={12} sm={6} style={{
            display: 'flex',
            justifyContent: 'end',
         }}>
            <TrafficMap resortName={resort.name}
               mapMode={'place'}
               lat={resort.ll[1]}
               long={resort.ll[0]} />
         </Grid>
         <Grid item xs={12} sm={6}>Lift Status goes here</Grid>
         <Grid item xs={12} sm={6}>Twitter Feed goes here</Grid>
      </Grid>
   );
}
